tinymce.PluginManager.add('rollups', function(editor, url) {
        editor.addMenuItem('unlink', {text: 'Remove link',
                                     icon: 'unlink',
                                     cmd: 'Unlink'});
        editor.addMenuItem('justifyleft', {text:'Left Justify',
	        							icon: 'alignleft',
	        							cmd: 'justifyleft'});
       editor.addMenuItem('justifycenter', {text:'Center',
	        							icon: 'aligncenter',
	        							cmd: 'justifycenter'});
	    editor.addMenuItem('justifyright', {text:'Right Justify',
	        							icon: 'alignright',
	        							cmd: 'justifyright'});
	        							
       editor.addButton('linksrollup',
                      {type   : 'MenuButton',
                       icon   : 'link',
                       title  : 'Link Options',
                       menu   : [editor.menuItems['link'],
                                 editor.menuItems['unlink'],
                                 editor.menuItems['anchor'],
                                 editor.menuItems['hr'],
                                 editor.menuItems['pagebreak'],
                                 editor.menuItems['insertdatetime']]
       });
       editor.addButton('insertrollup',
                      {type   : 'MenuButton',
                       icon   : 'image',
                       title  : 'Insert Options',
                       menu   : [editor.menuItems['image'],
                                 editor.menuItems['media'],
                                 editor.menuItems['pagebreak'],
                                 editor.menuItems['insertdatetime']]
       });  
        editor.addButton('coderollup',
                      {type   : 'MenuButton',
                       icon   : 'code',
                       title  : 'Tools',
                       menu   : [editor.menuItems['code'],
                                 editor.menuItems['visualblocks']]
       });   
        editor.addButton('pasterollup',
                      {type   : 'MenuButton',
                       icon   : 'paste',
                       title  : 'Paste Tools',
                       menu   : [editor.menuItems['paste'],
                                 editor.menuItems['pastetext'],
                                 editor.menuItems['pasteword']]
       });   
         editor.addButton('alignrollup',
                      {type   : 'MenuButton',
                       icon   : 'alignleft',
                       title  : 'Alignment Options',
                       menu   : [editor.menuItems['justifyleft'],
                                 editor.menuItems['justifycenter'],
                                 editor.menuItems['justifyright']
                                 ]
       }); 
});